function NewPlayer({ handleAddPlayer }) {
    const validationSchema = yup.object({
        first_name: yup.string()
          .min(2, 'Name must be at least 2 characters long'),
        last_name: yup.string()
          .min(2, 'Last name must be at least 2 characters long'),
        position: yup.string()
          .required('Position is required'),
        age: yup.number()
          .required('Age is required')
    })

    const formik = useFormik({
        initialValues: {
            first_name: '',
            last_name: '',
            position: '',
            age: '',
            roster_id: '',
        },
        validationSchema,
        enableReinitialize: true,
        onSubmit: (values, { setSubmitting, resetForm }) => {
            fetch('/players', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(values),
                })
            .then(res => {
                return res.json();
            })
            .then(data => {
                handleAddPlayer()
                resetForm();
            })
            .catch(() => {
              return("nope")
            })
            .finally(() => {
              setSubmitting(false)
            }
          );
        },
    });


  return (
    <div className="signuppage">
    <form onSubmit={formik.handleSubmit} className="signupform">
      <h2 className="signuptitle">Create A New Player</h2>
      {formik.errors.first_name && <div>{formik.errors.first_name}</div>}
      <label>First Name: </label>
      <input
        className="input-signup"
        name="first_name"
        value={formik.values.first_name}
        onChange={formik.handleChange}
        placeholder="Enter First Name..."
      />
      <br/>
      {formik.errors.last_name && <div>{formik.errors.last_name}</div>}
      <label>Last Name: </label>
      <input
        className="input-signup"
        name="last_name"
        value={formik.values.last_name}
        onChange={formik.handleChange}
        placeholder="Enter Last Name..."
      />
      <br/>
      {formik.errors.position && <div>{formik.errors.position}</div>}
      <label>Position: </label>
      <input
        className="input-signup"
        name="position"
        value={formik.values.position}
        onChange={formik.handleChange}
        placeholder="Enter Position..."
      />
      <br/>
      {formik.errors.age && <div>{formik.errors.age}</div>}
      <label>Age: </label>
      <input 
        className="input-signup"
        name="age"
        value={formik.values.age}
        onChange={formik.handleChange}
        placeholder="Enter Age..."
        />
      <br/>
      <button className="signupbutton" type="submit" disabled={formik.isSubmitting} >
        Sign Up
      </button>
    </form>
    </div>
  );
}
export default NewPlayer;
