import { useState } from "react";
import PlayerList from "./PlayerList";
import EditRoster from "./EditRoster";

function Roster({ level, coach, roster_size, teamcity, teammascot, players, id, year, handleUpdateRoster }) {
    const [isEditing, setIsEditing] = useState(false)

    return (
        <div className="rosters">
            {isEditing ? (
                <EditRoster
                    id={id}
                    roster_size={roster_size}
                    level={level}
                    year={year}
                    handleUpdateRoster={handleUpdateRoster}
                />
            ):(
                <div>
                <p className="rosterteamtitle">Team Name: {teamcity} {teammascot}</p>
                <p>Coach Name: {coach}</p>
                <p>Level of Competition: {level}</p>
                <p>Roster Size: {roster_size}</p>
                <p>Players: </p>
                <PlayerList
                    players={players}
                />
                </div>
            )
        }
        </div>
    )}
export default Roster;
