import React, { createContext, useContext, useEffect, useState } from "react";
import { Switch, Route } from "react-router-dom";
import CoachList from "./CoachList";
import NavBar from "./Navbar";
import Home from "./Home";
import Login from "./Login";
import Signup from "./SignUp";
import Logout from "./Logout";
import TeamList from "./TeamList";
import NavbarLogIn from "./NavbarLogIn";

export const CoachContext = createContext(null);
export const TeamContext = createContext(null);
export const RosterContext = createContext(null);
export const LoginContext = createContext(null);


function App() {
    const [coaches, setCoaches] = useState([]);
    const [teams, setTeams] = useState([]);
    const [rosters, setRosters] = useState([]);
    const [login, setLogin] = useState(false);


    useEffect(() => {
        fetch('/coaches')
        .then((r) => r.json())
        .then((data) => setCoaches(data));
    }, [setCoaches]);

    useEffect(() => {
        fetch('/teams')
        .then((r) => r.json())
        .then((data) => setTeams(data));
    }, []);

    useEffect(() => {
        fetch('/rosters')
        .then((r) => r.json())
        .then((data) => setRosters(data));
    }, []);

    function onSignupSubmit(data) {
        setCoaches(...coaches, data)
    }

    function onNewTeam(data) {
        setTeams(...teams, data)
    }

    function onLogin() {
        setLogin((login) => !login)
    }

    return (
        <div>
            {login ? (
                <NavBar/>
            ) : (
                <NavbarLogIn />
            )}
            <Switch>
                <Route exact path="/coaches">
                    <CoachContext.Provider value={coaches}>
                        <CoachList />
                    </CoachContext.Provider>
                </Route>
                <Route exact path="/">
                    <Home />
                </Route>
                <Route exact path="/login">
                    <Login onLogin={onLogin} />
                </Route>
                <Route exact path="/signup">
                    <Signup onSignupSubmit={onSignupSubmit} onLogin={onLogin}/>
                </Route>
                <Route exact path="/logout">
                    <Logout onLogin={onLogin} login={login} />
                </Route>
                <Route exact path="/teams">
                    <TeamContext.Provider value={teams}>
                        <TeamList onNewTeam={onNewTeam} />
                    </TeamContext.Provider>
                </Route>
            </Switch>
        </div>
    )
}

export default App;
