import React, { createContext, useContext, useEffect, useState } from "react";
import { Switch, Route } from "react-router-dom";
import CoachList from "./CoachList";
import NavBar from "./Navbar";
import Home from "./Home";
import Login from "./Login";
import Signup from "./SignUp";
import Logout from "./Logout";
import TeamList from "./TeamList";
import NavbarLogIn from "./NavbarLogIn";
import NewTeam from "./NewTeam";
import NewRoster from "./NewRoster";
import RosterList from "./RosterList";

export const CoachContext = createContext(null);
export const TeamContext = createContext(null);
export const RosterContext = createContext(null);
export const LoginContext = createContext(null);


function App() {
    const [coaches, setCoaches] = useState([]);
    const [teams, setTeams] = useState([]);
    const [rosters, setRosters] = useState([]);
    const [login, setLogin] = useState(false);
    const [user, setUser] = useState([])


    useEffect(() => {
        fetch('/coaches')
        .then((r) => r.json())
        .then((data) => setCoaches(data));
    }, [setCoaches]);

    useEffect(() => {
        fetch('/teams')
        .then((r) => r.json())
        .then((data) => setTeams(data));
    }, []);

    useEffect(() => {
        fetch('/rosters')
        .then((r) => r.json())
        .then((data) => setRosters(data));
    }, [setRosters]);

    function onSignupSubmit(data) {
        setCoaches(...coaches, data)
    }

    function onNewTeam(data) {
        setTeams(...teams, data)
    }

    function onLogin() {
        setLogin((login) => !login)
    }

    function onNewRoster(data) {
        setRosters(...rosters, data)
    }




    return (
        <div>
            {login ? (
                <NavBar/>
            ) : (
                <NavbarLogIn />
            )}
            <Switch>
                <Route exact path="/coaches">
                    <CoachContext.Provider value={coaches}>
                        <CoachList />
                    </CoachContext.Provider>
                </Route>
                <Route exact path="/">
                    <Home login={login} setUser={setUser} user={user} />
                </Route>
                <Route exact path="/login">
                    <Login onLogin={onLogin}  />
                </Route>
                <Route exact path="/signup">
                    <Signup onSignupSubmit={onSignupSubmit} onLogin={onLogin}/>
                </Route>
                <Route exact path="/logout">
                    <Logout onLogin={onLogin} login={login} />
                </Route>
                <Route exact path="/teams">
                    <TeamContext.Provider value={teams}>
                        <TeamList onNewTeam={onNewTeam} login={login} user={user} />
                    </TeamContext.Provider>
                </Route>
                <Route exact path="/newteam">
                    <NewTeam />
                </Route>
                <Route exact path="/newroster">
                    <NewRoster onNewRoster={onNewRoster} user={user}/>
                </Route>
                <Route>
                    <RosterList rosters={rosters} user={user} />
                </Route>
            </Switch>
        </div>
    )
}

export default App;
