import { useState } from "react"

function Team({ city, mascot, theme, rosters, login, user }) {
    const [click, setClick] = useState(false)
    return (
        <div className="teamcontainer">
                {login ? ( 
                <ul>
                {rosters.map((roster) => {
                    if (roster.coach.id == user.id){
                        return(
                            <div key={roster.id} className="teamrosters">
                                <p>Team Name: {city} {mascot} </p>
                                <p>Year: {roster.year} </p>
                                <p>Level of Competition: {roster.level} </p>
                                <p>Coach Name: {roster.coach.first_name} {roster.coach.last_name} </p>
                            </div>
                        )
                    }
                })}
            </ul>
                ):(
                    <ul>
                    <p className="teamtitle">Team Name: {city} {mascot} </p>
                        <ul>
                            {rosters.map((roster) => {
                                if (!roster.year){
                                    return(<h3>No Current Rosters For This Team!</h3>)
                                }
                                if (theme === "sporty"){
                                    return(
                                    <div key={roster.id} className="sportyteamrosters">
                                        <div className="p">
                                        <p>Year: {roster.year} </p>
                                        <p>Level of Competition: {roster.level} </p>
                                        <p>Coach Name: {roster.coach.first_name} {roster.coach.last_name} </p>
                                        </div>
                                    </div>
                                )
                                }
                                if (theme === "dark"){
                                    return(
                                    <div key={roster.id} className="darkteamrosters">
                                        <div className="p">
                                        <p>Year: {roster.year} </p>
                                        <p>Level of Competition: {roster.level} </p>
                                        <p>Coach Name: {roster.coach.first_name} {roster.coach.last_name} </p>
                                        </div>
                                    </div>
                                )
                                }
                                if (theme === "light"){
                                    return(
                                    <div key={roster.id} className="lightteamrosters">
                                        <div className="p">
                                        <p>Year: {roster.year} </p>
                                        <p>Level of Competition: {roster.level} </p>
                                        <p>Coach Name: {roster.coach.first_name} {roster.coach.last_name} </p>
                                        </div>
                                    </div>
                                )
                                }
                                if (theme === "business"){
                                    return(
                                    <div key={roster.id} className="businessteamrosters">
                                        <div className="p">
                                        <p>Year: {roster.year} </p>
                                        <p>Level of Competition: {roster.level} </p>
                                        <p>Coach Name: {roster.coach.first_name} {roster.coach.last_name} </p>
                                        </div>
                                    </div>
                                )
                                }
                            })}
                        </ul>
                </ul>
                )}
        </div>
    )
}

export default Team;
