import React, { createContext, useContext, useEffect, useState } from "react";
import { Switch, Route } from "react-router-dom";
import CoachList from "./CoachList";
import NavBar from "./Navbar";
import Home from "./Home";
import Login from "./Login";
import Signup from "./SignUp";
import Logout from "./Logout";

export const CoachContext = createContext(null);
export const TeamContext = createContext(null);
export const RosterContext = createContext(null);


function App() {
    const [coaches, setCoaches] = useState([]);
    const [teams, setTeams] = useState([]);
    const [rosters, setRosters] = useState([]);
    const [login, setLogin] = useState({});


    useEffect(() => {
        fetch('/coaches')
        .then((r) => r.json())
        .then((data) => setCoaches(data));
    }, [setCoaches]);

    useEffect(() => {
        fetch('/teams')
        .then((r) => r.json())
        .then((data) => setTeams(data));
    }, []);

    function onSignupSubmit(data) {
        setCoaches(...coaches, data)
    }

    function onLogin(data) {
        setLogin(data)
    }

    return (
        <div>
            <NavBar/>
            <Switch>
                <Route exact path="/coaches">
                    <CoachContext.Provider value={coaches}>
                        <CoachList />
                    </CoachContext.Provider>
                </Route>
                <Route exact path="/">
                    <Home />
                </Route>
                <Route exact path="/login">
                    <Login onLogin={onLogin}/>
                </Route>
                <Route exact path="/signup">
                    <Signup onSignupSubmit={onSignupSubmit}/>
                </Route>
                <Route exact path="/logout">
                    <Logout />
                </Route>
            </Switch>
        </div>
    )
}

export default App;
