import React, { useEffect, useState, useRef} from "react";
import { Formik, useFormik } from 'formik';
import * as yup from "yup";


function Login({ onSignupSubmit }) {

    const validationSchema = yup.object({
        first_name: yup.string()
          .min(2, 'Name must be at least 2 characters long'),
        last_name: yup.string()
          .min(2, 'Last name must be at least 2 characters long'),
        playing_style: yup.string()
          .required('Playing Style is required'),
        username: yup.string()
          .required('Username is required'),
        password: yup.string()
          .required('Password is required')
    })

    const formik = useFormik({
        initialValues: {
            first_name: '',
            last_name: '',
            playing_style: '',
            username: '',
            password: '',
        },
        validationSchema,
        enableReinitialize: true,
        onSubmit: (values, { setSubmitting, resetForm }) => {
            fetch('/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(values),
                })
            .then(res => {
                if (!res.ok) {
                    throw new Error('Failed to create');
                }
                return res.json();
            })
            .then(data => {
                onSignupSubmit(data);
                resetForm();
            })
            .finally(() => setSubmitting(false));
        },
    });


  return (
    <form onSubmit={formik.handleSubmit} className="login">
      <h2 className="logintitle">Login</h2>
      {formik.errors.first_name && <div>{formik.errors.first_name}</div>}
      <label>First Name: </label>
      <input
        className="input-login"
        name="firstname"
        value={formik.values.first_name}
        onChange={formik.handleChange}
        placeholder="Enter First Name..."
      />
      <br/>
      {formik.errors.last_name && <div>{formik.errors.last_name}</div>}
      <label>Last Name: </label>
      <input
        className="input-login"
        name="lastname"
        value={formik.values.last_name}
        onChange={formik.handleChange}
        placeholder="Enter Last Name..."
      />
      <br/>
      {formik.errors.playing_style && <div>{formik.errors.playing_style}</div>}
      <label>Playing Style: </label>
      <input
        className="input-login"
        name="playingstyle"
        value={formik.values.playing_style}
        onChange={formik.handleChange}
        placeholder="Enter Playing Style..."
      />
      <br/>
      {formik.errors.username && <div>{formik.errors.username}</div>}
      <label>Username: </label>
      <input
        className="input-login"
        name="username"
        value={formik.values.username}
        onChange={formik.handleChange}
        placeholder="Enter Username"
      />
      <br/>
      <button className="loginbutton" type="submit" disabled={formik.isSubmitting} >
        Log in
      </button>
    </form>
  );
}
export default Login;
